classes.class:
public class TestClass {

	public static int a(int a) { 
		if (a==1) return 1;
		return 3;
	}

}

tests.test:
import static org.junit.Assert.*;
import org.junit.Test;

public class TestClassTest {

    @Test
    public void testSomething() {

	assertEquals(1,1);

	int x = TestClass.a(1);
	assertEquals(1,x);
    }

	@Test
    public void testSomething2() {

	int x = TestClass.a(3);
	assertEquals(3,x);






    }
}
