classes.class:
public class Class {
	public static int method2() {
		return 1;
	}

	public static int method5() {
		return 3;
	}


}

tests.test:
import static org.junit.Assert.*;
import org.junit.Test;

public class MainTest {


    @Test
    public void testSomething2() {
	int x = Class.method2();
	assertEquals(1, x);
	}

	@Test
    public void testSomething() {
	int x = Class.method5();
	assertEquals(4, x);
	}

}
